"use strict";$(document).ready((function(){var t=$(".slide-top").toArray();var e=$(".slide-bottom").toArray();$(".next").on("click",(function(){$(".next").css("pointer-events","none");$(".prev").css("pointer-events","none");var r=0;var i=$(t).last().width();for(var n=0;n<t.length-1;n++){var a=t[n];r+=$(a).width();movePicture($(a),i)}moveLastPicture($(t).last(),-r,i);t=createNewArray(t,1);var s=0;var o=$(e).last().width();for(var c=0;c<e.length-1;c++){var v=e[c];s+=$(v).width();movePicture($(v),o)}moveLastPicture($(e).last(),-s,o);e=createNewArray(e,1)}));$(".prev").on("click",(function(){$(".prev").css("pointer-events","none");$(".next").css("pointer-events","none");var r=0;var i=$(t).first().width();for(var n=1;n<t.length;n++){var a=t[n];r+=$(a).width();movePicture($(a),-i)}$(t).first().addClass("invisible");$(t).first().css("opacity","0");moveFirstPicture($(t).first(),r);t=createNewArray(t,0);var s=0;var o=$(e).first().width();for(var c=1;c<e.length;c++){var v=e[c];s+=$(v).width();movePicture($(v),-o)}$(e).first().addClass("invisible");$(e).first().css("opacity","0");moveFirstPicture($(e).first(),s);e=createNewArray(e,0)}))}));function movePicture(t,e){t.animate({left:"+=".concat(e)},400,"swing",(function(){$(".next").css("pointer-events","auto");$(".prev").css("pointer-events","auto")}))}function moveLastPicture(t,e,r){t.animate({left:"+=".concat(r)},400,"swing",(function(){t.css("opacity","0");t.animate({left:"+=".concat(e-r)},1,(function(){t.css("opacity","1")}))}))}function moveFirstPicture(t,e){t.delay(400).animate({left:"+=".concat(e)},1,"linear",(function(){t.animate({opacity:1},1,"swing",(function(){t.removeClass("invisible")}))}))}function createNewArray(t,e){var r=[];var i;if(e==1){r[0]=t.pop();for(i=1;i<=t.length;i++){r[i]=t[i-1]}}else{for(i=0;i<t.length-1;i++){r[i]=t[i+1]}r[t.length-1]=t[0]}return r}