"use strict";$(document).ready((function(){var e=$(".slide-top").toArray();var t=$(".slide-bottom").toArray();$(".next").on("click",(function(){$(".next").css("pointer-events","none");$(".prev").css("pointer-events","none");var r=0;var n=$(e).last().width();for(var o=0;o<e.length-1;o++){var a=e[o];r+=$(a).width();movePicture($(a),n)}movePicture($(e).last(),-r);e=createNewArray(e,1);var i=0;var v=$(t).last().width();for(var c=0;c<t.length-1;c++){var s=t[c];i+=$(s).width();movePicture($(s),v)}movePicture($(t).last(),-i);t=createNewArray(t,1)}));$(".prev").on("click",(function(){$(".prev").css("pointer-events","none");$(".next").css("pointer-events","none");var r=0;var n=$(e).first().width();for(var o=1;o<e.length;o++){var a=e[o];r+=$(a).width();movePicture($(a),-n)}movePicture($(e).first(),r);e=createNewArray(e,0);var i=0;var v=$(t).first().width();for(var c=1;c<t.length;c++){var s=t[c];i+=$(s).width();movePicture($(s),-v)}movePicture($(t).first(),i);t=createNewArray(t,0)}))}));function movePicture(e,t){e.animate({left:"+=".concat(t)},350,"swing",(function(){$(".next").css("pointer-events","auto");$(".prev").css("pointer-events","auto")}))}function createNewArray(e,t){var r=[];var n;if(t==1){r[0]=e.pop();for(n=1;n<=e.length;n++){r[n]=e[n-1]}}else{for(n=0;n<e.length-1;n++){r[n]=e[n+1]}r[e.length-1]=e[0]}return r}